mapper.select:
SELECT sm_no, sale_date, quantity, var_amount, total_amount, product_name, product_price, product_category FROM sale_manage JOIN product_name USING(product_code)
sale_date BETWEEN #{startDate} AND #{endDate}
AND product_category = #{productCategory}
AND product_name LIKE CONCAT ('%', #{productName}, '%')
ORDER BY sale_date
