mapper.update:
UPDATE sale
SET sale_date = #{saleDate}
WHERE sale_no = #{saleNo}

mapper.select:
SELECT s.sale_no, p.product_no, pn.product_code, pn.product_name, pn.product_price, pn.product_category, s.sale_date, s.dept_no, d.dept_name FROM sale s JOIN product p ON s.product_no = p.product_no JOIN product_name pn ON p.product_code = pn.product_code JOIN department d ON s.dept_no = d.dept_no WHERE s.sale_date IS NOT NULL
AND pn.product_category = #{productCategory}
AND s.sale_date BETWEEN #{startDate} AND #{endDate}
ORDER BY s.sale_date DESC
